mapper.update:
update student set id = #{id},
                   name = #{name},
                   no = #{no},
                   gender = #{gender},
                   phone = #{phone},
                   degree = #{degree},
                   violation_count = #{violationCount},
                   violation_score = #{violationScore},
                   clazz_id = #{clazzId},
                   update_time = #{updateTime}

                        ,id_card = #{idCard}


                        ,is_college = #{isCollege}


                        ,address = #{address}


                       ,graduation_date = #{graduationDate}

                   where id = #{id}

update student set violation_count = violation_count + 1,
                   violation_score = violation_score + #{score}
where id = #{id}
